INSERT INTO [dbo].[AddQuestions] ([Questionid], [QLevel], [Question], [Type], [OptionA], [OptionB], [OptionC], [OptionD], [Explaination], [Marks], [Answer]) VALUES (4, 1, N'HTML stands for?', N'MCQ', N'Hyper Text Markup Language', N'High Text Markup Language', N'Hyper Tabular Markup Language', N'None of these', N'Hypertext Markup Language is the standard markup language for creating web pages and web applications. With Cascading Style Sheets and JavaScript, it forms a triad of cornerstone technologies for the World Wide Web.', 1, N'OptionA')
