INSERT INTO [dbo].[AddQuestions] ([Questionid], [QLevel], [Question], [Type], [OptionA], [OptionB], [OptionC], [OptionD], [Explaination], [Marks], [Answer]) VALUES (6, 1, N'In HTML we use _____ tag to defines quotation marks around the text ?', N'BLANK', NULL, NULL, NULL, NULL, N'< q > tag is used for quotes.', 1, N'< q >')
